import React, {useState} from 'react';

import TitleScreen from './TitleScreen.js';
import NormalGame from './NormalGame.js';

const FlashMath = () => {
    const [difficulty, setDifficulty] = useState(0);

    const rems = [
        <TitleScreen changeRem={(type, diff) => startGame(type, diff)} difficulty={difficulty}/>, 
        <NormalGame changeRem={(type, diff) => startGame(type, diff)} difficulty={difficulty}/>
    ];
    const [rem, setRem] = useState(rems[0]);

    const startGame = (type, diff) => {
        setDifficulty(diff);
        console.log(diff);
        console.log(type);
        console.log(difficulty);
        setRem(rems[type]);
    }

    return (
        <>
            {rem}
        </>
    )
}

export default FlashMath;
